import { roundToNearest } from "../site.js";

//global variables
export let cookies = 0;
let cookiesGainedOnClick = 1;
export let cps = 0;

// Page Visibility API
let hidden, visibilityChange;
if (typeof document.hidden !== "undefined") {
  hidden = "hidden";
  visibilityChange = "visibilitychange";
} else if (typeof document.msHidden !== "undefined") {
  hidden = "msHidden";
  visibilityChange = "msvisibilitychange";
} else if (typeof document.webkitHidden !== "undefined") {
  hidden = "webkitHidden";
  visibilityChange = "webkitvisibilitychange";
}
let cookiesInterval;

export function cookieClicked() {
  cookies += cookiesGainedOnClick;
  updateGUI();
}

function updateGUI() {
  document.getElementById("cookie-counter").innerHTML = roundToNearest(cookies, 0);
  document.getElementById("cps-counter").innerHTML = roundToNearest(cps, 1);
}

export function increaseCookieCount(amount) {
  cookies += amount;
  updateGUI();
}

export function changeAmountOfCookies(amount) {
  cookies = amount;
  updateGUI();
}

export function changeCps(amount) {
  cps = amount;
}

export function calculateCPS() {
  cookies += cps / 100;
  updateGUI();
}

function startCookiesInterval() {
  cookiesInterval = setInterval(() => {
    if (!document[hidden]) {
      calculateCPS();
    }
  }, 10);
}

function stopCookiesInterval() {
  clearInterval(cookiesInterval);
}

document.addEventListener(visibilityChange, () => {
  if (document[hidden]) {
    stopCookiesInterval();
  } else {
    startCookiesInterval();
  }
});

startCookiesInterval();
