import { roundToNearest } from "../site.js";
import CpsWorker from "./cpsWorker"
//global variables

export let cookies = 0;
let cookiesGainedOnClick = 1;
export let cps = 0;

export function cookieClicked() {
    cookies += cookiesGainedOnClick;
    updateGUI();

}

function updateGUI() {
    document.getElementById("cookie-counter").innerHTML = roundToNearest(cookies, 0);
    document.getElementById("cps-counter").innerHTML = roundToNearest(cps, 1);
}
let cpsWorker = new CpsWorker();
let cpsIntervalId = null;

function startCpsInterval() {
  cpsWorker.postMessage("start");
  cpsWorker.onmessage = function(e) {
    let newCps = e.data;
    cps = newCps;
  };
}

function stopCpsInterval() {
  cpsWorker.postMessage("stop");
}

function updateCps() {
  cpsWorker.postMessage(cps);
}

export function loadGame() {
  // Load the saved game data
  let savedData = localStorage.getItem("gameSave");
  if (savedData) {
    let data = JSON.parse(savedData);
    cookies = data.cookies;
    cps = data.cps;
  }
  // Start the cps interval
  startCpsInterval();
}

export function saveGame() {
  // Save the game data
  let data = {
    cookies: cookies,
    cps: cps
  };
  localStorage.setItem("gameSave", JSON.stringify(data));
}

export function resetGame() {
  // Reset the game data
  cookies = 0;
  cps = 0;
  // Stop the cps interval
  stopCpsInterval();
}

export function increaseCookieCount(amount) {
  cookies += amount;
  updateCps();
}

export function changeAmountOfCookies(amount) {
  cookies = amount;
  updateCps();
}

export function changeCps(amount) {
  cps = amount;
  updateCps();
}

export function calculateCPS() {
    cookies += cps / 100;
    updateGUI();
}
